#!/usr/bin/env python
# -*- coding:utf-8 -*-
import base64,struct
bmp_data=base64.b64decode('')
def bmp_info(data):
    str=struct.unpack('<ccIIIIIIHH',data[:30]) #bytes类也有切片方法
    if str[0]==b'B' and str[1]==b'M':
        print("这是位图文件")
        return{
            'width':str[-4],
            'height':str[-3],
            'color':str[-1]
        }
    else:
        print("这不是位图文件")

if __name__ == '__main__':
    #测试
    bi=bmp_info(bmp_data)
    assert bi['width']==28
    assert bi['height']==10
    assert bi['color']==16
    print('ok')